all.map:
function(doc) {
	var id = doc._id.substr(2);
	var type = doc._id.substr(0,2);
	emit([id,type],null);
	if ( doc.userid ) {
		emit([doc.userid,type],null);
	}
	if (doc.login) {
		emit(["login",doc.login],null);
	}
}

topic.map:
function(doc) {
	var id = doc._id.substr(2);
	var type = doc._id.substr(0,2);

	if ( type == "se" ) {
		emit(doc._id,null);
		emit(doc._id,{_id: "pr"+doc.userid});
		emit(doc._id,{_id: "us"+doc.userid});
	} else if ( type == "us" ) {
		emit("us"+id,{_id: "pr"+id});
		emit("us"+id,null);
	}
